package assesment;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
			  int i,fact=1;  //declaring the variables
			  int number=5;//It is the number to calculate factorial    
			  for(i=1;i<=number;i++){    //for loop for calculating 
			      fact=fact*i;  //logic  
			  }    
			  System.out.println("Factorial of "+number+" is: "+fact);    //printing the value
			 }  
		


}
